INSERT INTO `roles` (`id`, `name`, `status`, `description`, `created_at`, `updated_at`) VALUES
(1, 'Giám đốc', 1, '<p>Người chịu trách nhiệm quản lý và điều hành toàn bộ hoạt động của doanh nghiệp. Có quyền truy cập đầy đủ vào hệ thống, bao gồm quản lý nhân sự, chi nhánh, báo cáo tài chính và thiết lập chiến lược phát triển.</p>', '2025-06-18 21:09:57', '2025-06-20 23:43:25'),
(2, 'Quản lý', 1, '<p style=\"text-align:justify;\">Quản lý có vai trò kiểm soát hoạt động nội bộ, đảm bảo quy trình làm việc diễn ra hiệu quả và tuân thủ chính sách chung của công ty. Được cấp quyền hạn truy cập vào dữ liệu kinh doanh, nhân sự và báo cáo để hỗ trợ công tác điều hành và đánh giá kết quả làm việc của nhân viên.</p>', '2025-06-19 01:25:37', '2025-06-20 04:31:51'),
(3, 'Thu ngân', 1, '<p style=\"text-align:justify;\">Chịu trách nhiệm thực hiện các giao dịch thanh toán với khách hàng, bao gồm tính tiền, in hóa đơn và xử lý các phương thức thanh toán. Đồng thời đảm bảo số liệu thu chi chính xác và hỗ trợ kiểm soát hàng hóa tại quầy.</p>', '2025-06-19 01:26:10', '2025-06-20 04:32:40'),
(5, 'Nhân viên bán hàng', 1, '<p>Chịu trách nhiệm tư vấn sản phẩm, hỗ trợ khách hàng trong quá trình mua sắm và hoàn tất đơn hàng. Đảm bảo trải nghiệm mua hàng tốt, đạt chỉ tiêu doanh số và phối hợp với các bộ phận liên quan để xử lý đơn hàng và tồn kho.</p><p>&nbsp;</p><p><br>&nbsp;</p>', '2025-06-19 03:09:33', '2025-06-19 03:09:33'),
